<?php/**	ডায়েরী - মুলপাতা*	লেখক : TR ROY***/require('dairy.php');echo '<div class="row">  <div class="col-md-6">  <br><a href="today.php" class="btn btn-primary">Today diary</a><br><a href="signout.php" class="btn btn-danger btn-xs">Signout</a><br><br>  </div>    <div class="col-md-6">	<h2>Practise Writing</h2>	<p>This is secure!</p>  </div></div>    ';if(isset($_GET['s'])){$search = mysql_real_escape_string($_GET['s']);$sk = htmlspecialchars($_GET['s']);;$sql = ("SELECT id,time FROM note WHERE (time LIKE '%$search%') or (note LIKE '%$search%')");}else{$sql = ("SELECT id,time FROM note");$sk = "";}$per_page= 10;//echo $sql;$count = mysql_num_rows(mysql_query($sql));if(isset($_GET['per_page'])){	$int_options = array("options"=>array("min_range"=>1));	if(filter_var($_GET['per_page'], FILTER_VALIDATE_INT, $int_options)){				$per_page = $_GET['per_page'];		}	else{		$die = ('<hr>invalid number of per page<hr>');	}}else{	$per_page = 10;}$current_page =0 ;if(isset($_GET['page'])){	$int_options = array("options"=>array("min_range"=>1));	if(!filter_var($_GET['page'], FILTER_VALIDATE_INT, $int_options)){			$die = ('<hr>Page Not found<hr>');	}	 $current_page = $_GET['page'];}else{	$current_page = 1 ;}$last_page = ceil($count/$per_page);$start = ($current_page-1)*$per_page;if($current_page>$last_page){	$die = ('<hr>No content in this page<hr>');}if($current_page==1){	$pre = '';}else{	$a = $current_page-1;	$pre=  " <a href='?page=$a&s=$sk&per_page=$per_page'> Previous </a> ";}if($current_page>=$last_page){	$nxt = '';}else{	$a = $current_page+1;	$nxt=  " <a href='?page=$a&s=$sk&per_page=$per_page'> Next </a> ";}/*echo "<form method='get' action='' class='form-inline'><input name='s' type='text' placeholder='Search' class='form-control'><input name='per_page' class='form-control'  type='text' value='$per_page'> <input type='submit' value='Submit' class='btn btn-large'></form><br><br>";*/if(isset($die)){	die($die);}// Now lets set the limit for our query$limit = "LIMIT $start, $per_page";//ORDER BY `info`.`id` DESC //echo $count. ' result <br>';$query = mysql_query("$sql ORDER BY `id` DESC $limit ");/////////////////////////////////////////if($count<1){	$die ('<h1>No Note found</h1>');}?><div class="row">  <div class="col-md-6">				<table class="table table-hover  table-striped">				<tr>					<td>#ID</td>					<td>Created Time</td>				</tr>				<?php while($note = mysql_fetch_array($query)){					$idhash = x0012x9($note['id'],date('d m y'));				?>				<tr>					<td ><?php echo $note['id']; ?> <a href="view.php?id=<?php echo $idhash; ?>"> View </a></td>					<td><?php echo $note['time']; ?></td>				</tr>				<?php } ?>				</table><?php			echo $pre;			echo $nxt;			?>        </div>  <div class="col-md-6">	<iframe src="today.php" style="width:100%;min-height:700px;border:1px solid #f1f1f1"></iframe>  </div></div>